sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    isDatePast: function(strDate) {
        var rightnow = new GlideDateTime();
        var whenNeeded = new GlideDateTime(strDate);
        if (whenNeeded.before(rightnow)) {
            return true;
        } else {
            return false;
        }
    },
    isDateToday: function(strDate) {
        var rightnow = new GlideDateTime();
        var whenNeeded = new GlideDateTime(strDate);

        var today = rightnow.getLocalDate();
        var istoday = whenNeeded.getLocalDate();
        if (istoday.compareTo(today) == 0) {
            return true;
        } else {
            return false;
        }
    },
    type: 'NeedItUtils'
};
